    ) VALUES (1455224, 'Ertugliflozin', 'Lithium carbonate', 'Moderate', 'Lithium may interfere with the therapeutic effects of insulin and other antidiabetic agents.', NULL, 'Blood glucose should be monitored more closely following initiation or discontinuation of lithium in patients receiving insulin or other antidiabetic agents.', NULL, 'Blood glucose should be monitored more closely following initiation or discontinuation of lithium in patients receiving insulin or other antidiabetic agents. If hypo- or hyperglycemia occur during lithium therapy, patients should initiate appropriate remedial therapy immediately and contact their physician. Dosage adjustments may be required if an interaction is suspected.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
