    ) VALUES (1455274, 'Halazepam', 'Lithium carbonate', 'Minor', 'Hypothermia has been reported in one patient receiving both diazepam and lithium. On rechallenge, the hypothermia was reproducible for the combination, but not for the drugs individually. It is not known whether other benzodiazepines will have similar interactions with lithium.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
