INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455511, 'Triamterene', 'Lithium carbonate', 'Major', 'Spironolactone and triamterene may increase serum lithium levels, resulting in a high risk of lithium toxicity. The mechanism is reduced renal clearance of lithium.', 'DDInter', 1767317476, 'Concurrent use of diuretics and lithium should generally be avoided. Extreme caution is recommended if they must be used together. Lithium levels should be monitored daily and lithium dose reductions may be required. Patients should be advised to notify their physician if they experience drowsiness, dizziness, weakness, ataxia, vomiting, diarrhea, tinnitus,tremor, thirst, blurry vision, or increased urination.', 'Metabolism', 'Concurrent use of diuretics and lithium should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455512, 'Triazolam', 'Lithium carbonate', 'Minor', 'Hypothermia has been reported in one patient receiving both diazepam and lithium. On rechallenge, the hypothermia was reproducible for the combination, but not for the drugs individually. It is not known whether other benzodiazepines will have similar interactions with lithium.', 'DDInter', 1767317476, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
