INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455540, 'Ziprasidone', 'Lithium carbonate', 'Moderate', 'Coadministration of lithium with neuroleptic agents, particularly haloperidol, has been associated with rare cases of an encephalopathic syndrome characterized by weakness, lethargy, fever, tremors, confusion, extrapyramidal symptoms, leukocytosis, and elevated liver enzymes and blood urea nitrogen. This syndrome may be similar to, or the same as, neuroleptic malignant syndrome. Other, more common central nervous system effects may also be increased, such as dizziness, drowsiness, confusion, difficulty concentrating, and impairment in thinking, judgment, and motor coordination.', 'DDInter', 1767317476, 'Close monitoring for central nervous system adverse effects is recommended when lithium is used with neuroleptic agents. Dosage adjustments or discontinuation of one or both drugs may be necessary if an interaction is suspected.', 'Synergism', 'Close monitoring for central nervous system adverse effects is recommended when lithium is used with neuroleptic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
