INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455547, 'Anisindione', 'Lixisenatide', 'Minor', 'Coadministration of a single 25 mg dose of warfarin with daily dosing of lixisenatide 20 mcg resulted in a 19% decrease in warfarin peak plasma concentration (Cmax) and a 7-hour delay in time to reach peak plasma concentration (Tmax), but had no effect on warfarin systemic exposure (AUC) or the International Normalized Ratio (INR). The mechanism of interaction may involve delayed absorption due to slowing of the gastric emptying rate by lixisenatide. Based on these observations, no dosage adjustment for warfarin is required when coadministered with lixisenatide. The interaction has not been studied with other oral vitamin K antagonists.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
