INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455636, 'Dextran (low molecular weight)', 'Omacetaxine mepesuccinate', 'Major', 'Coadministration of omacetaxine and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with omacetaxine is associated with a high frequency of thrombocytopenia.', 'DDInter', 1767317476, 'Concomitant use of other medications that interfere with platelet function or coagulation should be avoided if the patient''s platelet count is less than 50,000 per microliter. Close clinical and laboratory observation for bleeding complications is recommended during therapy. The CBC and platelet count should be monitored according to the manufacturer''s recommendations.', 'Synergism', 'Concomitant use of other medications that interfere with platelet function or coagulation should be avoided if the patient''s platelet count is less than 50,000 per microliter.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455637, 'Dextran (low molecular weight)', 'Pentoxifylline', 'Moderate', 'The use of pentoxifylline has been associated with bleeding and/or prolongation in prothrombin time. Theoretically, the risk of bleeding may be increased by concomitant treatment with anticoagulants or other agents that affect hemostasis such as nonsteroidal anti-inflammatory drugs (NSAIDs), platelet inhibitors, thrombin inhibitors, thrombolytic agents, and dextran. There have been postmarketing reports of increased anticoagulant activity in patients receiving vitamin K antagonists in combination with pentoxifylline.', 'DDInter', 1767317476, 'Caution is advised if pentoxifylline is administered with anticoagulants or other agents that affect hemostasis. Patients on vitamin K antagonists such as warfarin should have more frequent monitoring of INR following initiation or dosage change of pentoxifylline, while patients receiving other agents that affect hemostasis should have periodic examinations for bleeding including hematocrit and/or hemoglobin.', 'Synergism', 'Caution is advised if pentoxifylline is administered with anticoagulants or other agents that affect hemostasis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455638, 'Dextran (low molecular weight)', 'Prasugrel', 'Major', 'Concomitant use of prasugrel with other agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, dextran, nonsteroidal anti-inflammatory agents (chronic), or agents that commonly cause thrombocytopenia may increase the risk of bleeding. Prasugrel may be used with aspirin, heparin, or glycoprotein IIb/IIIa inhibitors.', 'DDInter', 1767317476, 'Caution is advised if prasugrel is used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hemorrhagic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if prasugrel is used in combination with other drugs that affect hemostasis.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455639, 'Dextran (low molecular weight)', 'Ramucirumab', 'Major', 'Coadministration of ramucirumab and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Ramucirumab can cause bleeding, including severe and sometimes fatal hemorrhagic complications.', 'DDInter', 1767317476, 'Concomitant use of ramucirumab and medications that interfere with platelet function or coagulation should be approached cautiously. Close clinical and laboratory observation for bleeding complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools. Ramucirumab should be permanently discontinued in patients who experience severe bleeding.', 'Synergism', 'Concomitant use of ramucirumab and medications that interfere with platelet function or coagulation should be approached cautiously.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455640, 'Dextran (low molecular weight)', 'Rivaroxaban', 'Major', 'Concomitant use of rivaroxaban with other agents that alter hemostasis such as aspirin, nonsteroidal anti-inflammatory drugs (NSAIDs), platelet aggregation inhibitors, other anticoagulants, thrombolytic agents, or drugs that cause thrombocytopenia may increase the risk of bleeding. In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during rivaroxaban therapy may also be increased by the concomitant use of other drugs that affect coagulation. The development of epidural and spinal hematoma can lead to long-term neurological injury or permanent paralysis.', 'DDInter', 1767317476, 'Caution is recommended if rivaroxaban must be used with other agents that alter hemostasis. Patients should be monitored for increased anticoagulant effects and bleeding complications. In patients undergoing neuraxial intervention, coadministration of these agents should be approached with caution and only after thorough assessment of risks and benefits. Besides bleeding complications, patients should also be monitored frequently for signs and symptoms of neurologic impairment such as midline back pain, sensory and motor deficits (numbness or weakness in lower limbs), and bowel or bladder dysfunction.', 'Synergism', 'Caution is recommended if rivaroxaban must be used with other agents that alter hemostasis.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455641, 'Dextran (low molecular weight)', 'Tinzaparin', 'Major', 'Drugs that can affect hemostasis such as dextran, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or other anticoagulants may potentiate the risk of bleeding complications associated with the use of a low molecular weight heparin (LMWH), heparinoid, or fondaparinux. In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during LMWH, heparinoid, or fondaparinux therapy may also be increased by the concomitant use of other drugs that affect coagulation. The development of epidural and spinal hematoma can lead to long-term or permanent paralysis.', 'DDInter', 1767317476, 'In general, any agent that can enhance the risk of hemorrhage including other anticoagulants should be discontinued prior to initiation of LMWH, heparinoid, or fondaparinux therapy. If coadministration is necessary, it should be undertaken with caution and only after thorough assessment of risks and benefits. Close clinical and laboratory observation for bleeding complications is recommended. Patients undergoing neuraxial intervention and treated with these agents should also be monitored frequently for signs and symptoms of neurologic impairment such as midline back pain, sensory and motor deficits (numbness or weakness in lower limbs), and bowel or bladder dysfunction. The optimal timing between the administration of anticoagulants and neuraxial procedures is not known.', 'Synergism', 'In general, any agent that can enhance the risk of hemorrhage including other anticoagulants should be discontinued prior to initiation of LMWH, heparinoid, or fondaparinux therapy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455642, 'Dextran (low molecular weight)', 'Vorapaxar', 'Major', 'Concomitant use of vorapaxar with other agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, dextran, nonsteroidal anti-inflammatory agents (chronic), or agents that commonly cause thrombocytopenia may increase the risk of bleeding.', 'DDInter', 1767317476, 'Caution is advised if vorapaxar is used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hemorrhagic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if vorapaxar is used in combination with other drugs that affect hemostasis.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455643, 'Dextran (low molecular weight)', 'Zanubrutinib', 'Major', 'Coadministration of zanubrutinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications.', 'DDInter', 1767317476, 'Concomitant use of zanubrutinib in patients receiving other medications that interfere with platelet function or coagulation should be approached with caution. Close clinical and laboratory monitoring for bleeding complications is recommended during therapy. Patients should be advised to promptly report any signs and symptoms of bleeding to their physician. Discontinue zanubrutinib if intracranial hemorrhage of any grade occurs.', 'Synergism', 'Concomitant use of zanubrutinib in patients receiving other medications that interfere with platelet function or coagulation should be approached with caution.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455644, 'Abarelix', 'Lofexidine', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', 'DDInter', 1767317476, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455645, 'Amoxapine', 'Lofexidine', 'Moderate', 'The concomitant administration of tricyclic antidepressants may theoretically reduce the effects of lofexidine and increase central nervous system depressant effects. In addition, lofexidine has been associated with asymptomatic prolongation of the QT interval in some patients. Coadministration with other agents that could prolong the QT interval, such as tricyclic antidepressants, may theoretically result in an elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsades de pointes, because of additive arrhythmogenic potential related to their effects on cardiac conduction.', 'DDInter', 1767317476, 'During concomitant use of these drugs, patients should be monitored for altered efficacy and excessive or prolonged CNS depression. Patients should be made aware of the possibility of additive effects (e.g., drowsiness, dizziness, lightheadedness, confusion) and be cautioned about driving, operating machinery, or performing other hazardous tasks. The manufacturer recommends that the concurrent use of lofexidine with other medications that can prolong the QT interval be avoided. If no alternatives are available, patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsades de pointes such as dizziness, palpitations, or syncope.', 'Others', 'During concomitant use of these drugs, patients should be monitored for altered efficacy and excessive or prolonged CNS depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455646, 'Amyl Nitrite', 'Lofexidine', 'Moderate', 'The hypotensive effects of lofexidine and antihypertensive agents may be additive. Postural hypotension may occur.', 'DDInter', 1767317476, 'Hemodynamic responses should be monitored during coadministration of lofexidine with antihypertensive agents, especially during the first few weeks of therapy.', 'Synergism', 'Hemodynamic responses should be monitored during coadministration of lofexidine with antihypertensive agents, especially during the first few weeks of therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455647, 'Astemizole', 'Lofexidine', 'Moderate', 'Lofexidine has been associated with asymptomatic prolongation of the QT interval in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Coadministration of lofexidine with other agents with hypotensive effects may result in additive effects on blood pressure and orthostasis.', 'DDInter', 1767317476, 'Caution is recommended if lofexidine is used in combination with other drugs that can prolong the QT interval. Periodic monitoring with on-treatment electrocardiograms and serum electrolytes (magnesium, potassium) should be considered. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if lofexidine is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455648, 'Azatadine', 'Lofexidine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317476, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455649, 'Bedaquiline', 'Lofexidine', 'Major', 'Bedaquiline can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Caution is recommended if bedaquiline is used in combination with other drugs that can prolong the QT interval. ECG and serum electrolytes, including potassium, magnesium and calcium, should be monitored before starting bedaquiline therapy and periodically during treatment in accordance with the product labeling. Hypokalemia, hypomagnesemia, and hypocalcemia must be corrected prior to bedaquiline administration.', 'Synergism', 'Caution is recommended if bedaquiline is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Major', 'pharmacodynamic');
