INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456284, 'Etravirine', 'Lorlatinib', 'Major', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of lorlatinib, which has been found to be metabolized primarily by CYP450 3A4 and UGT1A4 in vitro. In addition to the potential for diminished efficacy of lorlatinib, the risk of serious hepatotoxicity may be increased due to activation of the pregnane X receptor (PXR) by both lorlatinib and CYP450 3A4 inducers. The effect of concomitant use of moderate CYP450 3A4 inducers on lorlatinib pharmacokinetics or the risk of hepatotoxicity is unknown.', 'DDInter', 1767317476, 'Concomitant use of lorlatinib with moderate CYP450 3A4 inducers should generally be avoided. If coadministration is required, monitoring of AST, ALT and bilirubin is recommended 48 hours after initiating lorlatinib and at least 3 times during the first week of treatment. Depending upon the relative importance of each drug, lorlatinib or the CYP450 3A4 inducer should be discontinued for persistent Grade 2 or higher hepatotoxicity.', 'Metabolism', 'Concomitant use of lorlatinib with moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456285, 'Everolimus', 'Lorlatinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the blood concentrations and pharmacologic effects of everolimus.', 'DDInter', 1767317476, 'Some manufacturers recommend avoiding concomitant use of everolimus with moderate CYP450 3A4 and/or P-gp inducers such as bosentan, efavirenz, and nevirapine. If concomitant use is required, a dose adjustment of everolimus should be considered to achieve the recommended therapeutic range for the condition being treated. Everolimus whole blood trough levels should be closely monitored during treatment, particularly 2 weeks after a dose increase and 2 weeks after discontinuation of the inducer.', 'Metabolism', 'Some manufacturers recommend avoiding concomitant use of everolimus with moderate CYP450 3A4 and/or P-gp inducers such as bosentan, efavirenz, and nevirapine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456286, 'Exemestane', 'Lorlatinib', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of exemestane, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'The manufacturer recommends increasing the dosage of exemestane to 50 mg once daily when used with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort. However, it has also been suggested that suppression of estrogen levels is not affected by the interaction, thus dosage adjustment of exemestane is not required. The extent to which other, less potent CYP450 3A4 inducers may interact with exemestane is unknown. Caution is advised if they are used with exemestane.', 'Metabolism', 'The manufacturer recommends increasing the dosage of exemestane to 50 mg once daily when used with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456287, 'Fedratinib', 'Lorlatinib', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may decrease the plasma concentrations of fedratinib, which is a substrate of the isoenzyme.', 'DDInter', 1767317476, 'According to the product labeling, concomitant use of fedratinib with potent or moderate CYP450 3A4 inducers should be avoided.', 'Metabolism', 'According to the product labeling, concomitant use of fedratinib with potent or moderate CYP450 3A4 inducers should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456288, 'Felodipine', 'Lorlatinib', 'Moderate', 'Coadministration with lorlatinib may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The proposed mechanism is increased clearance due to lorlatinib-mediated induction of CYP450 3A4 metabolism.', 'DDInter', 1767317476, 'Caution is advised when lorlatinib is used concomitantly with drugs that are substrates of CYP450 3A4. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lorlatinib is added to or withdrawn from therapy. Avoid concomitant use with lorlatinib where minimal decreases in concentration of the CYP450 3A4 substrate may lead to serious therapeutic failure. If coadministration is required, the CYP450 3A4 substrate dosage should be increased in accordance with approved product labeling.', 'Metabolism', 'Caution is advised when lorlatinib is used concomitantly with drugs that are substrates of CYP450 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456289, 'Fesoterodine', 'Lorlatinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of fesoterodine''s active metabolite, 5-hydroxymethyl tolterodine, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Caution is advised when fesoterodine is used with CYP450 3A4 inducers. The possibility of diminished therapeutic effects should be considered.', 'Metabolism', 'Caution is advised when fesoterodine is used with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456290, 'Fexofenadine', 'Lorlatinib', 'Moderate', 'Coadministration of lorlatinib with drugs that are P-glycoprotein (P-gp) substrates may decrease the plasma concentration and pharmacologic effect of the P-gp substrate. The proposed mechanism is induction of P-gp mediated drug efflux by lorlatinib.', 'DDInter', 1767317476, 'Concomitant use of lorlatinib with drugs that are P-gp substrates should generally be avoided, especially those P-gp substrates for which minimal concentration changes may lead to serious therapeutic failures. If coadministration is required, consider dosage adjustments and clinical and laboratory monitoring whenever lorlatinib is added to or withdrawn from therapy.', 'Excretion', 'Concomitant use of lorlatinib with drugs that are P-gp substrates should generally be avoided, especially those P-gp substrates for which minimal concentration changes may lead to serious therapeutic failures.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456291, 'Flibanserin', 'Lorlatinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of flibanserin, which is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C19.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of flibanserin should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of flibanserin should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456292, 'Fluconazole', 'Lorlatinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lorlatinib, which has been found to be metabolized primarily by CYP450 3A4 and UGT1A4 in vitro. Increased exposure to lorlatinib may increase the risk of central nervous system adverse effects (e.g., seizures, hallucinations, changes in cognitive function, mood disorders, mental status changes, sleep impairment), hyperlipidemia (hypercholesterolemia and hypertriglyceridemia), PR interval prolongation and atrioventricular (AV) block, and interstitial lung disease/pneumonitis. The extent to which other, less potent CYP450 3A4 inhibitors may affect lorlatinib is unknown.', 'DDInter', 1767317476, 'Caution is advised when lorlatinib is used with CYP450 3A4 inhibitors. Patients should be monitored for the development of central nervous system and other adverse effects, and the dosing adjusted or discontinued in accordance with the lorlatinib product labeling.', 'Metabolism', 'Caution is advised when lorlatinib is used with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
