INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456382, 'Pasireotide', 'Lorlatinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lorlatinib, which has been found to be metabolized primarily by CYP450 3A4 and UGT1A4 in vitro. Increased exposure to lorlatinib may increase the risk of central nervous system adverse effects (e.g., seizures, hallucinations, changes in cognitive function, mood disorders, mental status changes, sleep impairment), hyperlipidemia (hypercholesterolemia and hypertriglyceridemia), PR interval prolongation and atrioventricular (AV) block, and interstitial lung disease/pneumonitis. The extent to which other, less potent CYP450 3A4 inhibitors may affect lorlatinib is unknown.', 'DDInter', 1767317476, 'Caution is advised when lorlatinib is used with CYP450 3A4 inhibitors. Patients should be monitored for the development of central nervous system and other adverse effects, and the dosing adjusted or discontinued in accordance with the lorlatinib product labeling.', 'Metabolism', 'Caution is advised when lorlatinib is used with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456383, 'Pazopanib', 'Lorlatinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of pazopanib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'The use of pazopanib in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort should generally be avoided. Alternative treatment lacking CYP450 3A4-inducing activity should be considered in patients receiving pazopanib; otherwise, pazopanib should not be used. Other known CYP450 3A4 inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, nafcillin, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they may interact with pazopanib is unknown.', 'Metabolism', 'The use of pazopanib in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456384, 'Pemigatinib', 'Lorlatinib', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may decrease the plasma concentrations of pemigatinib, which is primarily metabolized by the isoenzyme in vitro.', 'DDInter', 1767317476, 'Concomitant use of pemigatinib with potent or moderate CYP450 3A4 inducers should be avoided.', 'Metabolism', 'Concomitant use of pemigatinib with potent or moderate CYP450 3A4 inducers should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456385, 'Perampanel', 'Lorlatinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of perampanel, which is primarily metabolized by the isoenzyme. The interaction has been reported with known CYP450 3A4 inducers such as carbamazepine, oxcarbazepine, and phenytoin.', 'DDInter', 1767317476, 'According to the manufacturer, concomitant use of perampanel with strong CYP450 3A4 inducers should be avoided.', 'Metabolism', 'According to the manufacturer, concomitant use of perampanel with strong CYP450 3A4 inducers should be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456386, 'Pexidartinib', 'Lorlatinib', 'Major', 'Coadministration of pexidartinib, a moderate CYP450 3A4 inducer may decrease the plasma concentrations of lorlatinib and increase the risk of hepatotoxicity. Additionally, pexidartinib is a CYP450 3A4 substrate and coadministration with a moderate CYP450 3A4 inducer, such as lorlatinib may result in decreased plasma concentrations and therapeutic effects of pexidartinib. A risk of serious hepatotoxicity may exist due to activation of the pregnane X receptor (PXR) by CYP450 3A4 inducers. When a single 100 mg oral dose of lorlatinib was administered to 12 healthy volunteers on day 8 of treatment with the potent CYP450 3A4 inducer rifampin (600 mg once daily for 8 days), mean lorlatinib peak plasma concentration (Cmax) and systemic exposure (AUC) decreased by 76% and 85%, respectively. The AUC of the two active metabolites decreased by 92% and 59%. Severe hepatotoxicity was reported in 10 of the 12 subjects, with Grade 4 alanine aminotransferase (ALT) or aspartate aminotransferase (AST) elevations occurring in 50% of subjects, Grade 3 elevations in 33% of subjects, and Grade 2 elevations in 8% of subjects. ALT or AST elevations occurred within 3 days and returned to within normal limits within 7 to 34 days (median 15 days). The median time to recovery was 18 days in subjects with Grade 3 or 4 elevations and 7 days in subjects with Grade 2 elevations. Concomitant administration of rifampin with pexidartinib decreased pexidartinib Cmax and AUC by 33% and 65%, respectively. Data is not available on the effects of less potent CYP450 3A4 inducers on the pharmacokinetics of pexidartinib or lorlatinib, or the risk of hepatotoxicity.', 'DDInter', 1767317476, 'Concomitant use of pexidartinib and lorlatinib should generally be avoided. If coadministration is required, AST, ALT and bilirubin should be assessed 48 hours after initiating lorlatinib and at least 3 times during the first week of treatment. Depending upon the relative importance of each drug, lorlatinib or pexidartinib should be discontinued for persistent Grade 2 or higher hepatotoxicity. Additionally, patients should be monitored for diminished therapeutic effects of either agent.', 'Metabolism', 'Concomitant use of pexidartinib and lorlatinib should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456387, 'Phenytoin', 'Lorlatinib', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of lorlatinib, which has been found to be metabolized primarily by CYP450 3A4 and UGT1A4 in vitro. In addition to the potential for diminished efficacy of lorlatinib, the risk of serious hepatotoxicity may be increased due to activation of the pregnane X receptor (PXR) by both lorlatinib and CYP450 3A4 inducers.', 'DDInter', 1767317476, 'Concomitant use of lorlatinib with potent CYP450 3A4 inducers is considered contraindicated. Lorlatinib should not be initiated until after discontinuation of potent CYP450 3A4 inducers for at least 3 plasma half-lives.', 'Metabolism', 'Concomitant use of lorlatinib with potent CYP450 3A4 inducers is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
