INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456397, 'Praziquantel', 'Lorlatinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of praziquantel, which is a substrate of the isoenzyme.', 'DDInter', 1767317476, 'Concomitant use of praziquantel with CYP450 3A4 inducers should generally be avoided, since therapeutically effective blood levels of praziquantel may not be achieved. Alternative agents for schistosomiasis should be considered whenever possible in patients receiving treatment with a CYP450 3A4 inducer. Otherwise, clinical response to praziquantel should be closely monitored.', 'Metabolism', 'Concomitant use of praziquantel with CYP450 3A4 inducers should generally be avoided, since therapeutically effective blood levels of praziquantel may not be achieved.', NULL, NULL, 'Moderate', 'pharmacodynamic');
