INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456400, 'Pretomanid', 'Lorlatinib', 'Major', 'Coadministration of pretomanid with strong or moderate CYP450 3A4 inducers may decrease the plasma concentrations and antimicrobial effects of pretomanid, which has been shown to be a substrate of the isoenzyme.', 'DDInter', 1767317476, 'The use of pretomanid with strong or moderate CYP450 3A4 inducers should be avoided. Dosage adjustments as well as clinical and laboratory monitoring of pretomanid should be considered whenever a strong or moderate CYP450 3A4 inducer is added to or withdrawn from therapy.', 'Metabolism', 'The use of pretomanid with strong or moderate CYP450 3A4 inducers should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456401, 'Primidone', 'Lorlatinib', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of lorlatinib, which has been found to be metabolized primarily by CYP450 3A4 and UGT1A4 in vitro. In addition to the potential for diminished efficacy of lorlatinib, the risk of serious hepatotoxicity may be increased due to activation of the pregnane X receptor (PXR) by both lorlatinib and CYP450 3A4 inducers.', 'DDInter', 1767317476, 'Concomitant use of lorlatinib with potent CYP450 3A4 inducers is considered contraindicated. Lorlatinib should not be initiated until after discontinuation of potent CYP450 3A4 inducers for at least 3 plasma half-lives.', 'Metabolism', 'Concomitant use of lorlatinib with potent CYP450 3A4 inducers is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456402, 'Progesterone', 'Lorlatinib', 'Moderate', 'RECOMMENDED: Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of estrogens and progestins. Estrogens have been shown in in vitro and in vivo studies to be partially metabolized by CYP450 3A4, and other steroids including progestins are also believed to undergo metabolism by this isoenzyme.', 'DDInter', 1767317476, 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary. For patients receiving hormonal contraceptives, additional or alternative non-hormonal birth control may be advisable during concomitant therapy with CYP450 3A4 inducers. Additional or alternative non-hormonal birth control may be recommended beyond discontinuation of the CYP450 3A4 inducer(s).', 'Metabolism', 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
