INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456454, 'Tadalafil', 'Lorlatinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of tadalafil, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'For the treatment of benign prostatic hyperplasia and erectile dysfunction, no dosage adjustment of tadalafil is warranted during coadministration with CYP450 3A4 inducers. However, the possibility of diminished pharmacologic effects of tadalafil should be considered. For the treatment of pulmonary arterial hypertension, use of tadalafil should be avoided in patients chronically taking potent inducers of CYP450 3A4.', 'Metabolism', 'For the treatment of benign prostatic hyperplasia and erectile dysfunction, no dosage adjustment of tadalafil is warranted during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456455, 'Tasimelteon', 'Lorlatinib', 'Moderate', 'Coadministration with inducers of CYP450 1A2 and/or 3A4 may decrease the plasma concentrations of tasimelteon, which is primarily metabolized by these isoenzymes.', 'DDInter', 1767317476, 'The potential for diminished therapeutic effects of tasimelteon should be considered during coadministration with inducers of CYP450 1A2 and/or 3A4. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished therapeutic effects of tasimelteon should be considered during coadministration with inducers of CYP450 1A2 and/or 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456456, 'Tazemetostat', 'Lorlatinib', 'Major', 'Coadministration with moderate or potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of tazemetostat, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inducers should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered whenever possible.', 'Metabolism', 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456457, 'Telaprevir', 'Lorlatinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of lorlatinib, which has been found to be metabolized primarily by CYP450 3A4 and UGT1A4 in vitro. Increased exposure to lorlatinib may increase the risk of central nervous system adverse effects (e.g., seizures, hallucinations, changes in cognitive function, mood disorders, mental status changes, sleep impairment), hyperlipidemia (hypercholesterolemia and hypertriglyceridemia), PR interval prolongation and atrioventricular (AV) block, and interstitial lung disease/pneumonitis.', 'DDInter', 1767317476, 'Concomitant use of lorlatinib with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the initial dosage of lorlatinib should be reduced from 100 mg orally once daily to 75 mg orally once daily. In patients who have had a dosage reduction to 75 mg orally once daily due to adverse reactions, the lorlatinib dosage should be further reduced to 50 mg orally once daily upon initiation of a potent CYP450 3A4 inhibitor. After 3 plasma half-lives following discontinuation of the potent CYP450 3A4 inhibitor, the lorlatinib dosage may be increased to that used prior to initiation of the inhibitor.', 'Metabolism', 'Concomitant use of lorlatinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456458, 'Telithromycin', 'Lorlatinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of lorlatinib, which has been found to be metabolized primarily by CYP450 3A4 and UGT1A4 in vitro. Increased exposure to lorlatinib may increase the risk of central nervous system adverse effects (e.g., seizures, hallucinations, changes in cognitive function, mood disorders, mental status changes, sleep impairment), hyperlipidemia (hypercholesterolemia and hypertriglyceridemia), PR interval prolongation and atrioventricular (AV) block, and interstitial lung disease/pneumonitis.', 'DDInter', 1767317476, 'Concomitant use of lorlatinib with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the initial dosage of lorlatinib should be reduced from 100 mg orally once daily to 75 mg orally once daily. In patients who have had a dosage reduction to 75 mg orally once daily due to adverse reactions, the lorlatinib dosage should be further reduced to 50 mg orally once daily upon initiation of a potent CYP450 3A4 inhibitor. After 3 plasma half-lives following discontinuation of the potent CYP450 3A4 inhibitor, the lorlatinib dosage may be increased to that used prior to initiation of the inhibitor.', 'Metabolism', 'Concomitant use of lorlatinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456459, 'Telotristat ethyl', 'Lorlatinib', 'Major', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of lorlatinib, which has been found to be metabolized primarily by CYP450 3A4 and UGT1A4 in vitro. In addition to the potential for diminished efficacy of lorlatinib, the risk of serious hepatotoxicity may be increased due to activation of the pregnane X receptor (PXR) by both lorlatinib and CYP450 3A4 inducers. The effect of concomitant use of moderate CYP450 3A4 inducers on lorlatinib pharmacokinetics or the risk of hepatotoxicity is unknown.', 'DDInter', 1767317476, 'Concomitant use of lorlatinib with moderate CYP450 3A4 inducers should generally be avoided. If coadministration is required, monitoring of AST, ALT and bilirubin is recommended 48 hours after initiating lorlatinib and at least 3 times during the first week of treatment. Depending upon the relative importance of each drug, lorlatinib or the CYP450 3A4 inducer should be discontinued for persistent Grade 2 or higher hepatotoxicity.', 'Metabolism', 'Concomitant use of lorlatinib with moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456460, 'Temazepam', 'Lorlatinib', 'Moderate', 'Coadministration with lorlatinib may decrease the plasma concentrations of drugs that are substrates of the uridine diphosphate glucuronosyl transferase (UGT) enzymes. The proposed mechanism involves decreased metabolic clearance due to induction of UGT by lorlatinib.', 'DDInter', 1767317476, 'Caution is advised when lorlatinib is used concurrently with drugs that are known UGT substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lorlatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when lorlatinib is used concurrently with drugs that are known UGT substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
