INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1456579, 'Secobarbital', 'Losartan', 'Minor', 'Concomitant administration of phenobarbital and losartan has been reported to decrease the area under the plasma concentration-time curve (AUC) of losartan and its active metabolite by as much as 20%. The mechanism is thought to be induction of losartan metabolism by phenobarbital. While data is available for phenobarbital only, theoretically this interaction may occur with other barbiturates. The reduction in the AUC was not considered significant, however, clinical monitoring for evidence of altered losartan effect is recommended if these drugs are used together.', 'DDInter', 1767317476, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
