INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1457221, 'Tetrabenazine', 'Lumateperone', 'Major', 'Tetrabenazine causes central dopamine depletion by binding reversibly to human vesicular monoamine transporter type 2 (VMAT2) and interfering with presynaptic monoamine storage mechanisms. Coadministration of tetrabenazine in combination with neuroleptic agents or other dopamine antagonists (e.g., metoclopramide, amisulpride) may result in severe manifestations of dopamine deficiency. Tetrabenazine as well as many neuroleptic agents (e.g., asenapine, clozapine, droperidol, haloperidol, iloperidone, paliperidone, pimozide, phenothiazines, quetiapine, risperidone, sertindole, ziprasidone) and other dopamine antagonists (e.g., domperidone, amisulpride) have been associated with dose-related prolongation of the QT interval.', 'DDInter', 1767317476, 'The use of tetrabenazine in combination with neuroleptic agents or other dopamine antagonists should preferably be avoided. When coadministration is required, patients should be instructed to notify their physician if they experience extrapyramidal symptoms such as bradykinesia, hypertonia, rigidity, restlessness, and dysphagia. Clinicians, caregivers, and family members should be apprised of the risk of neuroleptic malignant syndrome and be alert to potential signs and symptoms such as mental status changes (e.g., mutism, catatonia, stupor, coma, agitation, confusion, hallucinations, delusions), autonomic instability, restlessness, rigidity, ataxia, myoclonus, hyperreflexia, tremors, diaphoresis, elevated creatine phosphokinase levels, and hyperpyrexia. Patients should seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Synergism', 'The use of tetrabenazine in combination with neuroleptic agents or other dopamine antagonists should preferably be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1457222, 'Thalidomide', 'Lumateperone', 'Moderate', 'Thalidomide can commonly cause drowsiness/sedation and may have additive effects with other drugs that cause central nervous system (CNS) depression.', 'DDInter', 1767317476, 'Caution is advised when thalidomide is used with other CNS depressants such as anticonvulsants, antihistamines, antidepressants, antipsychotics, anxiolytics, opioids, and sedatives/hypnotics. Patients should be advised to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities. Dosage reductions may be necessary. Thalidomide should preferably be administered at bedtime to reduce the impact of somnolence. Alcohol use is not recommended.', 'Synergism', 'Caution is advised when thalidomide is used with other CNS depressants such as anticonvulsants, antihistamines, antidepressants, antipsychotics, anxiolytics, opioids, and sedatives/hypnotics.', NULL, NULL, 'Moderate', 'pharmacodynamic');
