INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1457305, 'Esketamine', 'Lurasidone', 'Moderate', 'Concomitant use of esketamine with central nervous system (CNS) depressants may increase sedation and impairment of attention, judgment, thinking, reaction speed, and psychomotor skills.', 'DDInter', 1767317476, 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants. Due to the risk of delayed or prolonged sedation and other adverse effects, patients should be monitored for at least 2 hours after esketamine administration, followed by an assessment to determine when the patient is considered clinically stable and ready to leave the healthcare setting. Patients should be instructed not to engage in potentially hazardous activities, such as driving a motor vehicle or operating machinery, until the next day after a restful sleep.', 'Synergism', 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants.', NULL, NULL, 'Moderate', 'pharmacodynamic');
