INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1457397, 'Metyrosine', 'Lurasidone', 'Moderate', 'Coadministration of metyrosine in combination with haloperidol, phenothiazines, or other dopamine antagonists may increase the risk of extrapyramidal disorders. Extrapyramidal effects have been reported during metyrosine therapy. The mechanism is inhibition of catecholamine synthesis by metyrosine. In addition, sedative effects of both drugs may be increased.', 'DDInter', 1767317476, 'When concurrent use is required, patients should be monitored for extrapyramidal symptoms and instructed to notify their physician if they experience symptoms such as bradykinesia, hypertonia, rigidity, restlessness, and dysphagia. Patients should also avoid driving or operating hazardous machinery until they know how the medications affect them.', 'Synergism', 'When concurrent use is required, patients should be monitored for extrapyramidal symptoms and instructed to notify their physician if they experience symptoms such as bradykinesia, hypertonia, rigidity, restlessness, and dysphagia.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1457398, 'Miconazole', 'Lurasidone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lurasidone and its active metabolite, both of which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Pharmacologic response to lurasidone should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the lurasidone dosage adjusted as necessary. Patients should be monitored for potentially increased adverse effects such as dizziness, drowsiness, dry mouth, increased appetite, weight gain, extrapyramidal symptoms, tardive dyskinesia, hyperglycemia, dyslipidemia, hyperprolactinemia (galactorrhea, amenorrhea, gynecomastia), orthostatic hypotension, cognitive and motor impairment, seizures, dysphagia, and heat-related illnesses due to disruption of body temperature regulation.', 'Metabolism', 'Pharmacologic response to lurasidone should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the lurasidone dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
