INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1457601, 'Glycerol phenylbutyrate', 'Lurbinectedin', 'Moderate', 'Coadministration with a CYP450 3A4 inducer may decrease lurbinectedin systemic exposure, which may reduce its efficacy. No drug interaction studies are available for lurbinectedin, but in vitro data suggest that lurbinectedin is metabolized by the CYP450 3A4 isoenzyme.', 'DDInter', 1767317476, 'Clinical and laboratory monitoring is advised whenever a CYP450 3A4 inducer is added to or withdrawn from lurbinectedin therapy.', 'Metabolism', 'Clinical and laboratory monitoring is advised whenever a CYP450 3A4 inducer is added to or withdrawn from lurbinectedin therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1457602, 'Goldenseal', 'Lurbinectedin', 'Moderate', 'Coadministration with a CYP450 3A4 inhibitor may increase lurbinectedin systemic exposure, which may increase the incidence and severity of adverse reactions to lurbinectedin, such as myelosuppression and hepatotoxicity.', 'DDInter', 1767317476, 'Clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy with lurbinectedin. Patients should be advised to contact their physician if they experience symptoms suggestive of myelosuppression or hepatotoxicity, such as fever or any other signs of infection, unusual bruising or bleeding; or nausea or vomiting, or pain on the right side of the abdomen.', 'Metabolism', 'Clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy with lurbinectedin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1457603, 'Golimumab', 'Lurbinectedin', 'Major', 'The use of tumor necrosis factor (TNF) blockers with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Serious infections and sepsis, including fatalities, have been reported with the use of TNF blockers, particularly in patients on concomitant immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids. Concomitant use of TNF blockers with other immunosuppressants such as azathioprine or mercaptopurine may also increase the risk of a rare and often fatal cancer of white blood cells known as hepatosplenic T-Cell lymphoma (HSTCL).', 'DDInter', 1767317476, 'Patients receiving a TNF blocker alone or with other immunosuppressive or myelosuppressive agents should be monitored closely for the development of infections. TNF blocker therapy should be discontinued if a serious infection or sepsis occurs. Close monitoring for signs and symptoms of HSTCL (e.g., splenomegaly, hepatomegaly, abdominal pain, persistent fever, night sweats, weight loss) is also recommended during use of TNF blockers, particularly in combination with other immunosuppressants such as azathioprine and mercaptopurine.', 'Synergism', 'Patients receiving a TNF blocker alone or with other immunosuppressive or myelosuppressive agents should be monitored closely for the development of infections.', NULL, NULL, 'Major', 'pharmacodynamic');
