        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1457847, 'Niraparib', 'Lutetium Lu 177 dotatate', 'Moderate', 'Coadministration with other myelosuppressive agents may potentiate and prolong the bone marrow toxicity associated with niraparib. Myelodysplastic syndrome (MDS) and acute myeloid leukemia (AML) have been reported in patients treated with niraparib.', NULL, 'Caution is advised if niraparib is prescribed with other myelosuppressive agents.', NULL, 'Caution is advised if niraparib is prescribed with other myelosuppressive agents.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1457848, 'Ocrelizumab', 'Lutetium Lu 177 dotatate', 'Moderate', 'The use of the CD20-directed cytolytic antibody ocrelizumab with other immune-modulating or immunosuppressive therapy, including immunosuppressant doses of corticosteroids may increase the risk of infections.', NULL, 'The increased risk of additive immunosuppression should be considered if coadministering ocrelizumab with other immunosuppressive therapy.', NULL, 'The increased risk of additive immunosuppression should be considered if coadministering ocrelizumab with other immunosuppressive therapy. In addition, the prolonged immunosuppressant effects and mode of action of other immunosuppressant drugs such as daclizumab, fingolimod, natalizumab, teriflunomide, or mitoxantrone should be considered prior to starting ocrelizumab therapy. Patients should be advised to notify their doctor if they develop signs or symptoms of infection, including upper or lower respiratory tract infection, skin infection, herpes related infection, or PML.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1457849, 'Octreotide', 'Lutetium Lu 177 dotatate', 'Moderate', 'INTERVAL: Somatostatin and its analogs may interfere with the therapeutic effects of lutetium Lu 177 dotatate by competitively binding to somatostatin receptors, the site of action for the radiolabeled agent. Lutetium Lu 177 dotatate is internalized upon binding to somatostatin receptor expressing cells, including malignant somatostatin receptor-positive tumor cells, and subsequent beta emission from Lu 177 induces cellular damage by formation of free radicals in both target and neighboring cells.', NULL, 'Long-acting formulations of somatostatin analogs should be discontinued for at least 4 weeks and short-acting formulations for at least 24 hours prior to each dose of lutetium Lu 177 dotatate.', NULL, 'Long-acting formulations of somatostatin analogs should be discontinued for at least 4 weeks and short-acting formulations for at least 24 hours prior to each dose of lutetium Lu 177 dotatate. During treatment with lutetium Lu 177 dotatate, long-acting octreotide should be administered intramuscularly between 4 to 24 hours after each dose of lutetium Lu 177 dotatate (but not within 4 weeks of each subsequent dose of lutetium Lu 177 dotatate), while short-acting octreotide may be given as needed for symptomatic management (but not within 24 hours before each subsequent dose of lutetium Lu 177 dotatate). Following completion of lutetium Lu 177 dotatate treatment, long-acting octreotide should be continued every 4 weeks until disease progression or up to 18 months following treatment initiation.', 'Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1457850, 'Olaparib', 'Lutetium Lu 177 dotatate', 'Moderate', 'Coadministration with other myelosuppressive agents may potentiate and prolong the bone marrow toxicity associated with olaparib. Myelodysplastic syndrome (MDS) and acute myeloid leukemia (AML) have been reported in patients treated with olaparib.', NULL, 'Caution is advised if olaparib is prescribed with other myelosuppressive agents.', NULL, 'Caution is advised if olaparib is prescribed with other myelosuppressive agents. Complete blood counts should be performed at baseline and monthly thereafter. Do not start olaparib until patients have recovered from hematologic toxicity caused by previous chemotherapy.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
