INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1458176, 'Oxytetracycline', 'Magnesium chloride', 'Moderate', 'INTERVAL: Administration of a tetracycline with aluminum, calcium, or magnesium salts significantly decreases tetracycline serum concentrations. The proposed mechanism is chelation of tetracycline by the cation, forming an insoluble complex that is poorly absorbed from the gastrointestinal tract. The interaction has also been reported with parenteral doxycycline and oral antacids.', 'DDInter', 1767317476, 'The administration of tetracyclines and preparations containing aluminum, magnesium, or calcium should be separated by two to four hours.', 'Absorption', 'The administration of tetracyclines and preparations containing aluminum, magnesium, or calcium should be separated by two to four hours.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1458177, 'Pancuronium', 'Magnesium chloride', 'Moderate', 'Magnesium salts may enhance the effects of nondepolarizing neuromuscular blocking agents. One study showed a 25% increase in vecuronium potency after intravenous administration of 40 mg/kg of magnesium sulfate.', 'DDInter', 1767317476, 'Patients should be closely monitored for excessive or prolonged neuromuscular blockade and ventilatory support should be immediately available.', 'Synergism', 'Patients should be closely monitored for excessive or prolonged neuromuscular blockade and ventilatory support should be immediately available.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1458178, 'Paricalcitol', 'Magnesium chloride', 'Moderate', 'Use of magnesium-containing products with a vitamin D analog may increase the risk of hypermagnesemia, particularly in chronic renal dialysis patients, due to potentially additive pharmacologic effects. Chronic hypermagnesemia may have a role in the pathogenesis of adynamic bone disease in dialysis patients.', 'DDInter', 1767317476, 'Patients on chronic renal dialysis treated with a vitamin D analog should avoid magnesium-containing products.', 'Synergism', 'Patients on chronic renal dialysis treated with a vitamin D analog should avoid magnesium-containing products.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1458179, 'Penicillamine', 'Magnesium chloride', 'Moderate', 'INTERVAL: Oral administration of aluminum, copper, iron, zinc, magnesium, and possibly other minerals such as calcium may decrease the gastrointestinal absorption of penicillamine, and vice versa. The proposed mechanism involves chelation of penicillamine to polyvalent cations, which leads to formation of a nonabsorbable complex.', 'DDInter', 1767317476, 'Mineral supplements or other products containing polyvalent cations (e.g., antacids or preparations containing antacids such as didanosine buffered tablets or pediatric oral solution) should be administered at least two hours before or two hours after the penicillamine dose. In addition, pharmacologic response to penicillamine should be monitored more closely whenever these products are added to or withdrawn from therapy, and the penicillamine dosage adjusted as necessary.', 'Absorption', 'Mineral supplements or other products containing polyvalent cations (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1458180, 'Raltegravir', 'Magnesium chloride', 'Moderate', 'Coadministration with aluminum- and magnesium-containing antacids has been shown to reduce the oral bioavailability of raltegravir. The proposed mechanism is chelation of raltegravir by polyvalent cations, but changes in solubility and lipophilicity of raltegravir related to pH increases may also contribute.', 'DDInter', 1767317476, 'It is not known to what extent non-antacid aluminum and magnesium salts may interact with raltegravir. Until more information is available, it may be advisable to avoid taking raltegravir with any aluminum- and/or magnesium-containing product. According to the manufacturer, antacids containing calcium carbonate may be used without dose adjustment for raltegravir.', 'Absorption', 'It is not known to what extent non-antacid aluminum and magnesium salts may interact with raltegravir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
