    ) VALUES (1458555, 'Aminoglutethimide', 'Maraviroc', 'Moderate', 'Coadministration with inducers of the CYP450 3A4 isoenzyme and/or P-glycoprotein efflux transporter may decrease the plasma concentrations of maraviroc, which is a substrate of both.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if maraviroc is used with CYP450 3A4 and/or P-glycoprotein inducers.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if maraviroc is used with CYP450 3A4 and/or P-glycoprotein inducers. A dosage adjustment for maraviroc may be necessary if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
