    ) VALUES (1458925, 'Fosphenytoin', 'Mebendazole', 'Moderate', 'Coadministration with anticonvulsant drugs such as carbamazepine, phenytoin, phenobarbital, and primidone may decrease mebendazole levels. The mechanism is unknown, but induction of hepatic metabolism by these anticonvulsant drugs has been proposed.', NULL, 'Caution and monitoring for altered clinical efficacy are recommended if mebendazole is used concomitantly with anticonvulsant drugs such as carbamazepine, phenytoin, phenobarbital, or primidone in patients being treated for systemic helminthic infections.', NULL, 'Caution and monitoring for altered clinical efficacy are recommended if mebendazole is used concomitantly with anticonvulsant drugs such as carbamazepine, phenytoin, phenobarbital, or primidone in patients being treated for systemic helminthic infections. Dose adjustments or alternative treatments may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
