    ) VALUES (1458930, 'Dobutamine', 'Mecamylamine', 'Moderate', 'Limited data suggest that methyldopa and other sympatholytics may increase or prolong the pressor effect of sympathomimetics. Hypertension may result. Data exist for phenylpropanolamine and norepinephrine.', NULL, 'If these drugs must be coadministered, blood pressure should be closely monitored.', NULL, 'If these drugs must be coadministered, blood pressure should be closely monitored. Discontinuation of the sympathomimetic may be necessary if hypertension develops.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
