        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1458985, 'Vasopressin', 'Mecamylamine', 'Moderate', 'Use with ganglionic blocking agents may increase the effect of vasopressin on mean arterial blood pressure. Other ganglionic blocking agents may interact as well.', NULL, 'Hemodynamic monitoring is recommended and the dose of vasopressin should be adjusted accordingly if concomitantly used with ganglionic blocking agents.', NULL, 'Hemodynamic monitoring is recommended and the dose of vasopressin should be adjusted accordingly if concomitantly used with ganglionic blocking agents.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1458986, 'Verteporfin', 'Mecamylamine', 'Moderate', 'Drugs that decrease clotting, vasoconstriction, or platelet aggregation may interfere with the therapeutic effect of verteporfin. These agents may antagonize the physiologic responses to local damage of neovascular endothelium induced by activated verteporfin, which include the release of procoagulant and vasoactive factors that result in vessel occlusion. However, the clinical significance is unknown.', NULL, 'If possible, agents such as anticoagulants, thrombolytics, platelet inhibitors, nonsteroidal anti-inflammatory drugs, ginkgo, dong quai, and vasodilators should be discontinued prior to verteporfin therapy.', NULL, 'If possible, agents such as anticoagulants, thrombolytics, platelet inhibitors, nonsteroidal anti-inflammatory drugs, ginkgo, dong quai, and vasodilators should be discontinued prior to verteporfin therapy. Otherwise, the potential for diminished efficacy of verteporfin should be considered.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
