INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1459504, 'Medroxyprogesterone acetate', 'Lonafarnib', 'Moderate', 'Coadministration with lonafarnib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. Lonafarnib is a potent CYP450 3A time-dependent and mechanism-based inhibitor in vitro.', 'DDInter', 1767317476, 'Caution is advised when lonafarnib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lonafarnib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects. The prescribing information recommends avoiding concomitant use of lonafarnib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges when possible. These drugs include, but are not limited to: alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', 'Caution is advised when lonafarnib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1459505, 'Methylphenobarbital', 'Medroxyprogesterone acetate', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of medroxyprogesterone, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Pharmacologic response to medroxyprogesterone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary. When administered as the depot formulation for contraception, no dosage adjustment for medroxyprogesterone is currently recommended during coadministration with CYP450 3A4 inducers.', 'Metabolism', 'Pharmacologic response to medroxyprogesterone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1459506, 'Metreleptin', 'Medroxyprogesterone acetate', 'Moderate', 'Treatment with metreleptin, a recombinant analog of human leptin, may affect the pharmacokinetics of drugs that are primarily metabolized by CYP450 isoenzymes.', 'DDInter', 1767317476, 'Caution is advised when metreleptin is prescribed to patients receiving drugs with a narrow therapeutic index that are substrates of CYP450 isoenzymes, such as macrolide immunosuppressants, oral contraceptives, cyclosporine, theophylline, and warfarin. Clinical and laboratory monitoring are recommended following the initiation, discontinuation or change of dosage of metreleptin, and the individual dosage of the concomitant agents adjusted as needed.', 'Metabolism', 'Caution is advised when metreleptin is prescribed to patients receiving drugs with a narrow therapeutic index that are substrates of CYP450 isoenzymes, such as macrolide immunosuppressants, oral contraceptives, cyclosporine, theophylline, and warfarin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
