INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1459513, 'Neratinib', 'Medroxyprogesterone acetate', 'Moderate', 'RECOMMENDED: Coadministration with neratinib may reduce the efficacy of hormonal contraceptives. The mechanism for the potential interaction has not been established.', 'DDInter', 1767317476, 'Although the clinical significance of this interaction is unknown, caution is advised when neratinib is prescribed concomitantly with hormonal contraceptives because of the potential to cause fetal harm. Women should avoid becoming pregnant during treatment with neratinib and for up to 1 month after discontinuing treatment. Women using hormonal contraceptives should add a barrier method. In addition, men receiving neratinib should use a barrier method of contraception during treatment and for 3 months after discontinuing treatment.', 'Antagonism', 'Although the clinical significance of this interaction is unknown, caution is advised when neratinib is prescribed concomitantly with hormonal contraceptives because of the potential to cause fetal harm.', NULL, NULL, 'Moderate', 'pharmacodynamic');
