INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1459518, 'Pegaspargase', 'Medroxyprogesterone acetate', 'Moderate', 'Theoretically, there is potential for an indirect interaction between asparaginase and oral contraceptives. The mechanism may involve asparaginase''s hepatotoxicity, which may impair oral contraceptives'' hepatic clearance.', 'DDInter', 1767317476, 'The use of oral contraceptives during asparaginase treatment is not recommended. Other non-oral contraceptive methods should be used in women of childbearing potential. According to the manufacturer, females of reproductive potential should be advised to use effective contraception during asparaginase treatment and for at least 3 to 6 months after the last dose.', 'Synergism', 'The use of oral contraceptives during asparaginase treatment is not recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
