INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1459523, 'Pioglitazone', 'Medroxyprogesterone acetate', 'Minor', 'Coadministration with another thiazolidinedione, troglitazone, has been shown to decrease the plasma concentrations of contraceptive hormones. The proposed mechanism is induction of hormone metabolism via CYP450 3A4.There are no data on the use of contraceptive hormones with pioglitazone. Because pioglitazone may be a weak inducer of CYP450 3A4, product labeling recommends additional caution in women requiring contraception.Intrauterine systems are unlikely to be significantly affected because of their local action.', 'DDInter', 1767317476, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
