INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1459525, 'Pralsetinib', 'Medroxyprogesterone acetate', 'Moderate', 'RECOMMENDED: Coadministration with pralsetinib may render hormonal contraceptives ineffective. The mechanism for the potential interaction has not been established and there are no clinical data regarding the use of pralsetinib with hormonal contraceptives. Pralsetinib can cause fetal harm when administered to a pregnant woman.', 'DDInter', 1767317476, 'Until further data are available, females of reproductive potential should be advised to use effective non-hormonal contraception during treatment and for 2 weeks after the final dose. In addition, men receiving pralsetinib should use effective contraception during treatment and for 1 week after the final dose.', 'Others', 'Until further data are available, females of reproductive potential should be advised to use effective non-hormonal contraception during treatment and for 2 weeks after the final dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1459526, 'Pramlintide', 'Medroxyprogesterone acetate', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317476, 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
