    ) VALUES (1460606, 'Fosphenytoin', 'Meperidine', 'Moderate', 'Phenytoin may induce the metabolism of meperidine to normeperidine, the latter of which has been associated with adverse effects such as lethargy, agitation, and seizures. The interaction may decrease the therapeutic effects of meperidine while increasing its adverse effects.', NULL, 'Meperidine product labeling recommends that the combination of meperidine and phenytoin be avoided.', NULL, 'Meperidine product labeling recommends that the combination of meperidine and phenytoin be avoided. The same precaution may be applicable to other hydantoins, although clinical data are lacking.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
