INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1460875, 'Midostaurin', 'Methylphenobarbital', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of midostaurin and its active metabolites, which are all substrates of the isoenzyme.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of midostaurin should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of midostaurin should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1460876, 'Mifepristone', 'Methylphenobarbital', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of mifepristone, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'When mifepristone is prescribed daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, concomitant use with potent CYP450 3A4 inducers such as carbamazepine, phenobarbital, phenytoin, and rifamycins should generally be avoided. Other, less potent inducers such as aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, nafcillin, nevirapine, somatrem, somatropin, and various other anticonvulsants may also interact. Caution is advised if they are used with mifepristone.', 'Metabolism', 'When mifepristone is prescribed daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, concomitant use with potent CYP450 3A4 inducers such as carbamazepine, phenobarbital, phenytoin, and rifamycins should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1460877, 'Milnacipran', 'Methylphenobarbital', 'Moderate', 'The efficacy of anticonvulsants may be diminished during coadministration with selective serotonin reuptake inhibitors (SSRIs) or serotonin-norepinephrine reuptake inhibitor (SNRIs). Antidepressants including SSRIs and SNRIs can reduce seizure threshold.Coadministration of SSRIs or SNRIs with some anticonvulsants, particularly carbamazepine, eslicarbazepine, oxcarbazepine and valproic acid, may increase the risk of hyponatremia. The risk appears to be dose-related, and elderly patients and patients who are volume depleted (e.g., diuretic use) may be at greater risk.', 'DDInter', 1767317476, 'SSRIs and SNRIs should be avoided in patients with unstable epilepsy, and used cautiously in patients with epilepsy controlled with anticonvulsant medications. Treatment with SSRIs and SNRIs should be discontinued if seizures develop or seizure frequency increases. Patients receiving SSRIs or SNRIs with anticonvulsants, particularly carbamazepine, eslicarbazepine, oxcarbazepine and/or valproic acid, should also have serum sodium levels measured regularly and monitored for development of hyponatremia, particularly when higher dosages of these medications are used. Signs and symptoms of hyponatremia include nausea, vomiting, headache, difficulty concentrating, memory impairment, confusion, malaise, lethargy, muscle weakness or spasms, and unsteadiness. In more severe and/or acute cases, hallucination, syncope, seizure, coma, respiratory arrest, and death may occur.', 'Antagonism', 'SSRIs and SNRIs should be avoided in patients with unstable epilepsy, and used cautiously in patients with epilepsy controlled with anticonvulsant medications.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1460878, 'Mirabegron', 'Methylphenobarbital', 'Minor', 'Coadministration with inducers of CYP450 3A4 and/or P-glycoprotein may decrease the plasma concentrations of mirabegron, which has been shown in vitro to be a substrate of the isoenzyme and efflux transporter. No dosage adjustment is recommended when mirabegron is administered in combination with rifampin and probably other CYP450 3A4/P-gp inducers.', 'DDInter', 1767317476, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1460879, 'Mirtazapine', 'Methylphenobarbital', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317476, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1460880, 'Morphine (liposomal)', 'Methylphenobarbital', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', 'DDInter', 1767317476, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery.', 'Synergism', 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1460881, 'Nefazodone', 'Methylphenobarbital', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317476, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1460882, 'Nelfinavir', 'Methylphenobarbital', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1460883, 'Nicardipine', 'Methylphenobarbital', 'Moderate', 'Barbiturates may increase the hepatic metabolism of some dihydropyridine calcium channel blockers by inducing CYP450 3A4 hepatic metabolism. The serum concentrations and effects of these calcium channel blockers may be significantly decreased.', 'DDInter', 1767317476, 'If concurrent administration cannot be avoided, the patient should be closely monitored for changes in pharmacologic effects whenever a barbiturate is initiated, discontinued or its dosage adjusted. An increased dosage of the calcium channel blocker may be necessary.', 'Metabolism', 'If concurrent administration cannot be avoided, the patient should be closely monitored for changes in pharmacologic effects whenever a barbiturate is initiated, discontinued or its dosage adjusted.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1460884, 'Nilotinib', 'Methylphenobarbital', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of nilotinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Patients receiving nilotinib with CYP450 3A4 inducers should be monitored for potentially diminished pharmacologic response to nilotinib. Concomitant use should be avoided if an interaction is suspected.', 'Metabolism', 'Patients receiving nilotinib with CYP450 3A4 inducers should be monitored for potentially diminished pharmacologic response to nilotinib.', NULL, NULL, 'Moderate', 'pharmacodynamic');
