INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1460917, 'Phenelzine', 'Methylphenobarbital', 'Moderate', 'Limited clinical data suggest that nonselective monoamine oxidase inhibitors (MAOIs) may enhance or prolong the central nervous system effects of barbiturates. The mechanism of interaction is unknown, but may involve inhibition of barbiturate metabolism by MAOIs. Somnolence, dizziness, ataxia, and semi-consciousness have been reported.', 'DDInter', 1767317476, 'Until more information is available, patients should be monitored for increased barbiturate effects during coadministration with an MAOI or other agents that possess MAOI activity such as furazolidone, linezolid, methylene blue, and procarbazine.', 'Metabolism', 'Until more information is available, patients should be monitored for increased barbiturate effects during coadministration with an MAOI or other agents that possess MAOI activity such as furazolidone, linezolid, methylene blue, and procarbazine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1460918, 'Phenytoin', 'Methylphenobarbital', 'Moderate', 'Barbiturates may induce or inhibit the metabolism of phenytoin. Plasma phenytoin concentrations may be decreased or increased. The mechanism are induction of hepatic CYP450 phenytoin metabolism by therapeutic doses of barbiturates, and competitive inhibition of metabolism by large doses.', 'DDInter', 1767317476, 'Close laboratory and clinical observation for altered effects of both agents is recommended when doses of either agent are started, changed, or discontinued. Patients should be advised to notify their physician if they experience loss of seizure control or excessive or prolonged sedative effects. It may be necessary to alter the dosage of one or both of the drugs.', 'Metabolism', 'Close laboratory and clinical observation for altered effects of both agents is recommended when doses of either agent are started, changed, or discontinued.', NULL, NULL, 'Moderate', 'pharmacodynamic');
