INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1460928, 'Procyclidine', 'Methylphenobarbital', 'Moderate', 'Central anticholinergic agents may have additive central nervous system (CNS) effects with cannabinoids, barbiturates, opiates, and alcohol. These agents individually can cause cognitive and psychomotor impairment, drowsiness, and dizziness, thus concomitant use may result in more potent effects. In addition, the potential for abuse may be increased when central anticholinergic agents are combined with these drugs.', 'DDInter', 1767317476, 'Patients taking central anticholinergic agents in combination with other CNS depressants should be monitored for potentially excessive or prolonged CNS depression, especially if they are elderly or debilitated. Ambulatory patients should be made aware of the possibility of additive CNS effects (e.g., drowsiness, dizziness, lightheadedness, confusion) and counseled to avoid activities requiring mental alertness.', 'Synergism', 'Patients taking central anticholinergic agents in combination with other CNS depressants should be monitored for potentially excessive or prolonged CNS depression, especially if they are elderly or debilitated.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1460929, 'Progesterone', 'Methylphenobarbital', 'Moderate', 'RECOMMENDED: Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of estrogens and progestins. Estrogens have been shown in in vitro and in vivo studies to be partially metabolized by CYP450 3A4, and other steroids including progestins are also believed to undergo metabolism by this isoenzyme.', 'DDInter', 1767317476, 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary. For patients receiving hormonal contraceptives, additional or alternative non-hormonal birth control may be advisable during concomitant therapy with CYP450 3A4 inducers. Additional or alternative non-hormonal birth control may be recommended beyond discontinuation of the CYP450 3A4 inducer(s).', 'Metabolism', 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1460930, 'Progesterone (topical)', 'Methylphenobarbital', 'Moderate', 'RECOMMENDED: Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of estrogens and progestins. Estrogens have been shown in in vitro and in vivo studies to be partially metabolized by CYP450 3A4, and other steroids including progestins are also believed to undergo metabolism by this isoenzyme.', 'DDInter', 1767317476, 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary. For patients receiving hormonal contraceptives, additional or alternative non-hormonal birth control may be advisable during concomitant therapy with CYP450 3A4 inducers. Additional or alternative non-hormonal birth control may be recommended beyond discontinuation of the CYP450 3A4 inducer(s).', 'Metabolism', 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
