INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1460986, 'Thalidomide', 'Methylphenobarbital', 'Moderate', 'Thalidomide can commonly cause drowsiness/sedation and may have additive effects with other drugs that cause central nervous system (CNS) depression.', 'DDInter', 1767317476, 'Caution is advised when thalidomide is used with other CNS depressants such as anticonvulsants, antihistamines, antidepressants, antipsychotics, anxiolytics, opioids, and sedatives/hypnotics. Patients should be advised to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities. Dosage reductions may be necessary. Thalidomide should preferably be administered at bedtime to reduce the impact of somnolence. Alcohol use is not recommended.', 'Synergism', 'Caution is advised when thalidomide is used with other CNS depressants such as anticonvulsants, antihistamines, antidepressants, antipsychotics, anxiolytics, opioids, and sedatives/hypnotics.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1460987, 'Thiethylperazine', 'Methylphenobarbital', 'Moderate', 'Concomitant use of phenothiazines with barbiturates may reduce the plasma concentrations and therapeutic effects of both agents. The mechanism is unclear but may relate to metabolic induction of CYP450 hepatic enzymes. In addition, coadministration of phenothiazines and barbiturates may lead to a reduction in seizure threshold and additive central nervous system (CNS) and respiratory depressant effects.', 'DDInter', 1767317476, 'Administration of phenothiazines in patients receiving large doses of barbiturates is considered contraindicated by some authorities (US). If coadministration is required, caution as well as clinical and laboratory monitoring should be considered whenever either of these agents is added to or withdrawn from therapy. If chlorpromazine is coadministered with a barbiturate, the manufacturer of chlorpromazine recommends using 1/4 to 1/2 of the usual barbiturate dosage. Patients should be monitored for hypotension, loss of seizure control and the development of adverse effects including CNS and respiratory depression.', 'Others', 'Administration of phenothiazines in patients receiving large doses of barbiturates is considered contraindicated by some authorities (US).', NULL, NULL, 'Moderate', 'pharmacodynamic');
