INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1460999, 'Toremifene', 'Methylphenobarbital', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of toremifene. According to the product labeling, toremifene is primarily metabolized by CYP450 3A4 to N-demethyltoremifene, an antiestrogenic metabolite with weak in vivo antitumor potency but whose serum concentrations are 2 to 4 times higher than those of toremifene at steady state. The extent to which other, less potent CYP450 3A4 inducers may affect toremifene is unknown.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of toremifene should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments or a dose adjustment of toremifene may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of toremifene should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1461000, 'Trabectedin', 'Methylphenobarbital', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of trabectedin, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'The possibility of diminished therapeutic effects should be considered when trabectedin is prescribed with CYP450 3A4 inducers. Close clinical and laboratory monitoring is recommended whenever a CYP450 3A4 inducer is added to or withdrawn from trabectedin therapy, and the dosage adjusted if necessary.', 'Metabolism', 'The possibility of diminished therapeutic effects should be considered when trabectedin is prescribed with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1461001, 'Tranylcypromine', 'Methylphenobarbital', 'Moderate', 'Limited clinical data suggest that nonselective monoamine oxidase inhibitors (MAOIs) may enhance or prolong the central nervous system effects of barbiturates. The mechanism of interaction is unknown, but may involve inhibition of barbiturate metabolism by MAOIs. Somnolence, dizziness, ataxia, and semi-consciousness have been reported.', 'DDInter', 1767317476, 'Until more information is available, patients should be monitored for increased barbiturate effects during coadministration with an MAOI or other agents that possess MAOI activity such as furazolidone, linezolid, methylene blue, and procarbazine.', 'Metabolism', 'Until more information is available, patients should be monitored for increased barbiturate effects during coadministration with an MAOI or other agents that possess MAOI activity such as furazolidone, linezolid, methylene blue, and procarbazine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
