INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1461042, 'Dicoumarol', 'Mercaptopurine', 'Moderate', 'Limited clinical data suggest that the purine analog antimetabolites, azathioprine and mercaptopurine, may reduce the hypoprothrombinemic response to warfarin and other oral anticoagulants. There have been case reports of patients stabilized on oral anticoagulant therapy whose prothrombin time or INR decreased following addition of the antimetabolites and returned to normal only after their discontinuation or an increase in anticoagulant dosage.', 'DDInter', 1767317476, 'Given the potential for interaction and the narrow therapeutic index of oral anticoagulants, patients should be closely monitored during concomitant therapy with azathioprine or mercaptopurine. The INR should be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of antimetabolite therapy in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of blood clots (e.g. chest pain, shortness of breath, sudden loss of vision, or pain, redness or swelling in an extremity).', 'Antagonism', 'Given the potential for interaction and the narrow therapeutic index of oral anticoagulants, patients should be closely monitored during concomitant therapy with azathioprine or mercaptopurine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1461043, 'Doxorubicin (liposomal)', 'Mercaptopurine', 'Moderate', 'The concomitant administration of mercaptopurine with doxorubicin may increase the risk of hepatotoxicity. The mechanism is unknown. Hepatotoxic effects have been reported in patients receiving doxorubicin and high doses of intravenous mercaptopurine. The applicability to therapy with oral mercaptopurine is unknown.', 'DDInter', 1767317476, 'If concomitant therapy is needed, it is recommended to monitor patients for clinical and laboratory signs and symptoms of hepatotoxicity.', 'Synergism', 'If concomitant therapy is needed, it is recommended to monitor patients for clinical and laboratory signs and symptoms of hepatotoxicity.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1461044, 'Efalizumab', 'Mercaptopurine', 'Moderate', 'The use of efalizumab with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Efalizumab alone can cause serious infections requiring hospitalization, and the risk may theoretically increase when coadministered with other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', 'DDInter', 1767317476, 'The safety and efficacy of efalizumab in combination with other immunosuppressive agents or phototherapy have not been evaluated. Patients receiving other immunosuppressive agents should not be treated with efalizumab because of the possibility of excessive immunosuppression. The duration of the period following treatment with efalizumab before one should consider starting other immunosuppressive therapy has not been evaluated.', 'Synergism', 'The safety and efficacy of efalizumab in combination with other immunosuppressive agents or phototherapy have not been evaluated.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1461045, 'Enoxacin', 'Mercaptopurine', 'Minor', 'Limited data suggest that chemotherapy with antineoplastic agents may reduce the plasma concentrations of oral quinolone antibiotics. The proposed mechanism is decreased quinolone absorption secondary to alteration of intestinal mucosa by cancer chemotherapy.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
