INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1461049, 'Febuxostat', 'Mercaptopurine', 'Major', 'Coadministration with febuxostat may significantly increase the plasma concentrations of xanthine oxidase substrates such as azathioprine, mercaptopurine, and theophylline. Toxicity may result. The proposed mechanism is inhibition of xanthine oxidase by febuxostat. Although drug interaction studies have not been conducted with febuxostat and xanthine oxidase substrates, the interaction has been reported with allopurinol, another xanthine oxidase inhibitor. Severe bone marrow suppression and other toxicities have been associated with concomitant use of allopurinol and mercaptopurine (6-MP) or azathioprine. Increased theophylline concentrations have also been reported during coadministration with allopurinol at dosages of 600 mg/day or greater.', 'DDInter', 1767317476, 'The use of febuxostat in patients treated with azathioprine, mercaptopurine, or theophylline is considered contraindicated.', 'Metabolism', 'The use of febuxostat in patients treated with azathioprine, mercaptopurine, or theophylline is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1461050, 'Filgrastim', 'Mercaptopurine', 'Moderate', 'INTERVAL: The safety and efficacy of hematopoietic growth factors such as colony-stimulating factors (G-CSF and GM-CSF) and stem cell factors (SCF) given simultaneously with cancer chemotherapy have not been established. Theoretical concerns exist regarding their concomitant administration because hematopoietic growth factors stimulate myeloid cell proliferation while antineoplastic agents primarily target rapidly dividing cells.', 'DDInter', 1767317476, 'Because of the potential sensitivity of rapidly dividing myeloid cells to cancer chemotherapy, hematopoietic growth factors should not be used within 24 hours before or 24 hours after administration of antineoplastic agents.', 'Antagonism', 'Because of the potential sensitivity of rapidly dividing myeloid cells to cancer chemotherapy, hematopoietic growth factors should not be used within 24 hours before or 24 hours after administration of antineoplastic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
