INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1461491, 'Ammonium chloride', 'Metamfetamine', 'Moderate', 'Urinary acidifying agents such as ammonium chloride, sodium acid phosphate, and methenamine salts may lower the blood levels and efficacy of amphetamines. Acidifying the urine has been reported to increase the concentration of the ionized species of the amphetamine molecule, thereby inhibiting renal tubular absorption and increasing its urinary excretion.', 'DDInter', 1767317476, 'Patients receiving an amphetamine in combination with urinary acidifying agents should be monitored for potentially diminished pharmacologic response to the amphetamine.', 'Excretion', 'Patients receiving an amphetamine in combination with urinary acidifying agents should be monitored for potentially diminished pharmacologic response to the amphetamine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1461492, 'Berotralstat', 'Metamfetamine', 'Moderate', 'Coadministration with berotralstat may increase the plasma concentrations and effects of drugs that are substrates of CYP450 3A4 and/or 2D6. The mechanism is decreased clearance due to inhibition of CYP450 3A4 and 2D6 activity by berotralstat.', 'DDInter', 1767317476, 'Caution is advised when berotralstat is coadministered with drugs that are substrates of CYP450 3A4 and/or 2D6, particularly those with a narrow therapeutic index including but not limited to thioridazine, pimozide, cyclosporine, and fentanyl.', 'Metabolism', 'Caution is advised when berotralstat is coadministered with drugs that are substrates of CYP450 3A4 and/or 2D6, particularly those with a narrow therapeutic index including but not limited to thioridazine, pimozide, cyclosporine, and fentanyl.', NULL, NULL, 'Moderate', 'pharmacodynamic');
