INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1461516, 'Haloperidol', 'Metamfetamine', 'Moderate', 'Butyrophenone neuroleptics may antagonize the pharmacologic effects of amphetamine, amphetamine derivatives, and other centrally-acting sympathomimetic agents (i.e., CNS stimulants). Conversely, these agents may diminish the neuroleptic efficacy of butyrophenones. The exact mechanism of interaction is unknown but may involve opposing effects on dopaminergic activity.', 'DDInter', 1767317476, 'Amphetamine, amphetamine derivatives, and other CNS stimulants should generally not be used, particularly for weight reduction, in patients treated with a butyrophenone neuroleptic agent.', 'Antagonism', 'Amphetamine, amphetamine derivatives, and other CNS stimulants should generally not be used, particularly for weight reduction, in patients treated with a butyrophenone neuroleptic agent.', NULL, NULL, 'Moderate', 'pharmacodynamic');
