INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1461571, 'Safinamide', 'Metamfetamine', 'Moderate', 'Monoamine oxidase inhibitors (MAOIs) may theoretically potentiate the cardiovascular effects of sympathomimetic agents following oral and/or topical administration (e.g., nasal decongestants, ophthalmic vasoconstrictors) by inhibiting the breakdown of catecholamines. There have been occasional reports of increased blood pressure and heart rate in patients receiving systemic direct-acting agents and MAOIs.', 'DDInter', 1767317476, 'Caution and more frequent blood pressure monitoring may be advisable when oral and/or topical sympathomimetic agents are coadministered with MAOIs including safinamide.', 'Metabolism', 'Caution and more frequent blood pressure monitoring may be advisable when oral and/or topical sympathomimetic agents are coadministered with MAOIs including safinamide.', NULL, NULL, 'Moderate', 'pharmacodynamic');
