INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1461900, 'Pentoxifylline', 'Methoxsalen', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 may increase the plasma concentrations of pentoxifylline and its active hydroxy metabolite (M1), both of which are substrates of the isoenzyme.', 'DDInter', 1767317476, 'Pharmacologic response to pentoxifylline should be monitored more closely whenever a potent or moderate CYP450 1A2 inhibitor is added to or withdrawn from therapy, and the pentoxifylline dosage adjusted as necessary. Patients should be advised to seek medical attention if they experience serious adverse effects such as bleeding, angina, or arrhythmias.', 'Metabolism', 'Pharmacologic response to pentoxifylline should be monitored more closely whenever a potent or moderate CYP450 1A2 inhibitor is added to or withdrawn from therapy, and the pentoxifylline dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
