INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1461904, 'Porfimer sodium', 'Methoxsalen', 'Moderate', 'Concomitant use of porfimer with drugs known to cause photosensitivity may increase the risk of a photosensitivity reaction. Photosensitivity with porfimer is due to residual drug being present in the skin. Patients treated with porfimer will be photosensitive for at least 30 days.', 'DDInter', 1767317476, 'Concomitant use of photosensitizing agents with porfimer should be avoided for at least 30 days and maybe up to 90 days or longer depending on patient''s hepatic and/or renal function.', 'Synergism', 'Concomitant use of photosensitizing agents with porfimer should be avoided for at least 30 days and maybe up to 90 days or longer depending on patient''s hepatic and/or renal function.', NULL, NULL, 'Moderate', 'pharmacodynamic');
