INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1461908, 'Rasagiline', 'Methoxsalen', 'Major', 'Coadministration with inhibitors of CYP450 1A2 may increase the plasma concentrations of rasagiline, which is primarily metabolized by the isoenzyme. Because the MAO-B selectivity of rasagiline may diminish with increasing dosage above the recommended range of 0.5 to 1 mg/day, there may be an increased risk of hypertensive crisis and other adverse reactions associated with nonselective inhibition of MAO if the maximum dosage is used in the presence of ciprofloxacin or other CYP450 1A2 inhibitors.', 'DDInter', 1767317476, 'Rasagiline should be prescribed at a maximum dosage of 0.5 mg/day during coadministration with moderate or potent CYP450 1A2 inhibitors. Patients should be advised to promptly seek medical attention if they experience potential signs and symptoms of a hypertensive crisis such as severe headache, visual disturbances, confusion, stupor or coma, seizures, chest pain, unexplained nausea or vomiting, and stroke-like symptoms.', 'Metabolism', 'Rasagiline should be prescribed at a maximum dosage of 0.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1461909, 'Sarecycline', 'Methoxsalen', 'Moderate', 'Concomitant use of methoxsalen with other known photosensitizing agents may increase the risk of photosensitivity reactions. These agents include anthralin, coal tar or coal tar derivatives, griseofulvin, hypericin extracts (e.g., St John''s Wort), fluoroquinolones, phenothiazines, retinoids, halogenated salicylanilides (bacteriostatic soaps), sulfonamides, sulfonylureas, tetracyclines, thiazides, and certain organic staining dyes such as methylene blue, toluidine blue, rose bengal, and methyl orange.', 'DDInter', 1767317476, 'Caution is advised and pharmacologic response to methoxsalen therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided. Patients should be advised to avoid sun exposure, even through window glass or cloud cover, for at least 8 hours after methoxsalen ingestion and during the 24 hours following photochemotherapy or photopheresis treatment.', 'Synergism', 'Caution is advised and pharmacologic response to methoxsalen therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
