INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1461992, 'Potassium chloride', 'Methscopolamine', 'Major', 'Concomitant use of agents with anticholinergic properties (e.g., antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, the class IA antiarrhythmic disopyramide) may potentiate the risk of upper gastrointestinal injury associated with oral solid formulations of potassium chloride. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents, thereby creating a high localized concentration of potassium ions in the region of a dissolving tablet or capsule and increasing the contact time with GI mucosa. Solid formulations of potassium chloride have been associated with upper GI bleeding and small bowel ulceration, stenosis, perforation, and obstruction.', 'DDInter', 1767317476, 'The use of certain oral solid formulations of potassium chloride is considered contraindicated in patients receiving agents with anticholinergic properties at sufficient doses to exert anticholinergic effects. A liquid formulation of potassium chloride should be considered. Patients prescribed a solid oral formulation should be advised to discontinue potassium therapy and contact their physician if they experience potential symptoms of upper GI injury such as severe vomiting, abdominal pain, distention, and gastrointestinal bleeding.', 'Others', 'The use of certain oral solid formulations of potassium chloride is considered contraindicated in patients receiving agents with anticholinergic properties at sufficient doses to exert anticholinergic effects.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1461993, 'Pramlintide', 'Methscopolamine', 'Moderate', 'The use of pramlintide in combination with drugs that alter gastrointestinal motility (e.g., loperamide; anticholinergic agents) or drugs that slow the intestinal absorption of nutrients (e.g., alpha-glucosidase inhibitors) has not been studied.', 'DDInter', 1767317476, 'Due to its effect on gastric emptying, pramlintide therapy should not be considered for patients taking drugs that alter gastrointestinal motility or drugs that slow the intestinal absorption of nutrients.', 'Absorption', 'Due to its effect on gastric emptying, pramlintide therapy should not be considered for patients taking drugs that alter gastrointestinal motility or drugs that slow the intestinal absorption of nutrients.', NULL, NULL, 'Moderate', 'pharmacodynamic');
