    ) VALUES (1462586, 'Tetrabenazine', 'Metoclopramide', 'Major', 'Coadministration of metoclopramide with phenothiazines, neuroleptics, or other antidopaminergic agents (e.g., tetrabenazine) may increase the frequency and severity of extrapyramidal reactions (i.e., acute dystonic reactions, tardive dyskinesia, akathisia, Parkinson-like symptoms) due to additive antidopaminergic effects.', NULL, 'Due to the potential for increased risk of serious and potentially irreversible extrapyramidal reactions, metoclopramide should not be prescribed in combination with other antidopaminergic agents.', NULL, 'Due to the potential for increased risk of serious and potentially irreversible extrapyramidal reactions, metoclopramide should not be prescribed in combination with other antidopaminergic agents. In addition, metoclopramide should not be used for longer than 12 weeks except in rare cases where therapeutic benefit is anticipated to outweigh the risk of developing tardive dyskinesia.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
