INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1462803, 'Trihexyphenidyl', 'Metolazone', 'Minor', 'Anticholinergic agents may increase the absorption and oral bioavailability of thiazide diuretics. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
