        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1462911, 'Midodrine', 'Metoprolol', 'Moderate', 'Midodrine, an alpha-1 adrenergic agent may lead to bradycardia if administered concomitantly with any agent that directly or indirectly reduces heart rate. This may be most important for beta blockers and tricyclic antidepressants.', NULL, 'Patients should be advised to discontinue midodrine if they experience signs or symptoms of decreased heart rate (i.', NULL, 'Patients should be advised to discontinue midodrine if they experience signs or symptoms of decreased heart rate (i.e., slow pulse, dizziness, syncope, cardiac awareness) and to contact their provider for reevaluation.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
