INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1463437, 'Entrectinib', 'Miconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of entrectinib, which is primarily metabolized by the isoenzyme. Increased exposure to entrectinib may increase the risk and/or severity of adverse effects such as cognitive impairment, mood disorders, dizziness, sleep disturbances, liver enzyme elevations, hyperuricemia, congestive heart failure, edema, myocarditis, QT prolongation, vision problems, anemia, and neutropenia.', 'DDInter', 1767317476, 'Caution is advised when entrectinib is used with CYP450 3A4 inhibitors. Pharmacologic response to entrectinib should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the entrectinib dosage adjusted as necessary in accordance with the product labeling. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised when entrectinib is used with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1463438, 'Enzalutamide', 'Miconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 and/or 3A4 may increase the plasma concentrations of enzalutamide, which is primarily metabolized by CYP450 2C8 to its pharmacologically active metabolite, N-desmethyl enzalutamide, and to a lesser extent by CYP450 3A4.', 'DDInter', 1767317476, 'Pharmacologic response to enzalutamide should be monitored more closely whenever a CYP450 2C8 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary. Patients should be advised to contact their physician if they experience increased side effects such as seizure, asthenia, fatigue, diarrhea, arthralgia, musculoskeletal pain, paresthesia, hot flushes, peripheral edema, headache, dizziness, insomnia, hematuria, anxiety, and hypertension.', 'Metabolism', 'Pharmacologic response to enzalutamide should be monitored more closely whenever a CYP450 2C8 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1463439, 'Eplerenone', 'Miconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 including azole antifungal agents may increase the plasma concentrations of eplerenone, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Caution is advised if eplerenone is prescribed in combination with miconazole mucous membrane preparations. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever miconazole is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if eplerenone is prescribed in combination with miconazole mucous membrane preparations.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1463440, 'Ergometrine', 'Miconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 including azole antifungal agents may increase the plasma concentrations of ergot derivatives, which are primarily metabolized by the isoenzyme. The interaction has occurred in patients receiving ergotamine or dihydroergotamine with potent CYP450 3A4 inhibitors such as macrolide antibiotics and protease inhibitors. Clinical ergotism has been reported, which can lead to gangrene and myocardial infarction in severe cases. Even small, single doses of ergotamine have resulted in clinically significant interactions, occasionally resulting in surgical amputation or death.', 'DDInter', 1767317476, 'Concomitant use of ergot derivatives with fluconazole at high, multiple doses or clotrimazole and miconazole mucous membrane preparations should generally be avoided, if possible. Otherwise, close monitoring for adverse effects such as peripheral vasospasm, ischemia, thrombosis, tachycardia, and hypertension is recommended. Patients should be advised to seek medical attention if they experience potential signs and symptoms of ergotism such as paresthesia, tingling of fingers, cold or painful extremities, numbness, muscle cramps, abdominal pain, rapid pulse, or chest pain.', 'Metabolism', 'Concomitant use of ergot derivatives with fluconazole at high, multiple doses or clotrimazole and miconazole mucous membrane preparations should generally be avoided, if possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1463441, 'Ergotamine', 'Miconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 including azole antifungal agents may increase the plasma concentrations of ergot derivatives, which are primarily metabolized by the isoenzyme. The interaction has occurred in patients receiving ergotamine or dihydroergotamine with potent CYP450 3A4 inhibitors such as macrolide antibiotics and protease inhibitors. Clinical ergotism has been reported, which can lead to gangrene and myocardial infarction in severe cases. Even small, single doses of ergotamine have resulted in clinically significant interactions, occasionally resulting in surgical amputation or death.', 'DDInter', 1767317476, 'Concomitant use of ergot derivatives with fluconazole at high, multiple doses or clotrimazole and miconazole mucous membrane preparations should generally be avoided, if possible. Otherwise, close monitoring for adverse effects such as peripheral vasospasm, ischemia, thrombosis, tachycardia, and hypertension is recommended. Patients should be advised to seek medical attention if they experience potential signs and symptoms of ergotism such as paresthesia, tingling of fingers, cold or painful extremities, numbness, muscle cramps, abdominal pain, rapid pulse, or chest pain.', 'Metabolism', 'Concomitant use of ergot derivatives with fluconazole at high, multiple doses or clotrimazole and miconazole mucous membrane preparations should generally be avoided, if possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
