INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1464407, 'Simvastatin', 'Mifepristone', 'Major', 'Coadministration with mifepristone may significantly increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by mifepristone.', 'DDInter', 1767317476, 'When mifepristone is used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, concomitant use of CYP450 3A4 substrates with narrow therapeutic ranges and high first-pass effect is considered contraindicated. These drugs include lovastatin, simvastatin, cyclosporine, ergot derivatives, fentanyl, oral midazolam, triazolam, pimozide, quinidine, sildenafil, sirolimus, and tacrolimus. The contraindication does not apply when mifepristone is used on a limited basis to terminate a pregnancy, but caution and close monitoring are advisable. Because mifepristone is eliminated slowly from the body, drug interactions may be observed for a prolonged period following discontinuation (approximately 2 to 3 weeks if mifepristone had been administered chronically to steady state).', 'Metabolism', 'When mifepristone is used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, concomitant use of CYP450 3A4 substrates with narrow therapeutic ranges and high first-pass effect is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
