INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1464418, 'St. John''s Wort', 'Mifepristone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of mifepristone, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'When mifepristone is prescribed daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, concomitant use with potent CYP450 3A4 inducers such as carbamazepine, phenobarbital, phenytoin, and rifamycins should generally be avoided. Other, less potent inducers such as aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, nafcillin, nevirapine, somatrem, somatropin, and various other anticonvulsants may also interact. Caution is advised if they are used with mifepristone.', 'Metabolism', 'When mifepristone is prescribed daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, concomitant use with potent CYP450 3A4 inducers such as carbamazepine, phenobarbital, phenytoin, and rifamycins should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1464419, 'Stiripentol', 'Mifepristone', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of stiripentol, which has been shown in vitro to undergo phase I metabolism via these pathways. Conversely, many of these inhibitors are also substrates of CYP450 1A2 and/or 3A4, and coadministration with stiripentol may alter their plasma concentrations as well. Stiripentol is both an inhibitor and inducer of CYP450 1A2 and 3A4 in vitro.', 'DDInter', 1767317476, 'Caution is advised when stiripentol is used with inhibitors of CYP450 1A2 and/or 3A4 that are also substrates of these enzymatic pathways. Patients should be monitored for potentially increased adverse effects of stiripentol such as anorexia, nausea, vomiting, weight loss, somnolence, dizziness, confusion, difficulty concentrating, ataxia, hypotonia, tremor, hyperkinesia, dysarthria, suicidal ideation/behavior, neutropenia, and thrombocytopenia. In addition, clinical and laboratory monitoring may be appropriate for concomitant drugs whenever stiripentol is added to or withdrawn from therapy. Dosage adjustments or alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'Caution is advised when stiripentol is used with inhibitors of CYP450 1A2 and/or 3A4 that are also substrates of these enzymatic pathways.', NULL, NULL, 'Moderate', 'pharmacodynamic');
