INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1464488, 'Acetohexamide', 'Miglitol', 'Moderate', 'Miglitol may potentiate the risk of hypoglycemia associated with the use of sulfonylureas or insulin. When administered alone, miglitol does not typically cause hypoglycemia in the fasted or postprandial state. However, because concomitant administration results in a further lowering of blood glucose, miglitol may increase the hypoglycemic potential of sulfonylureas and insulin.', 'DDInter', 1767317476, 'Dosage reduction of sulfonylureas and insulin should be considered when prescribed in combination with miglitol. Because miglitol inhibits the hydrolysis of sucrose to glucose and fructose, a source of glucose (dextrose, D-glucose) should be readily available to treat mild to moderate hypoglycemia. Severe hypoglycemia may require the use of either intravenous glucose infusion or glucagon injection', 'Synergism', 'Dosage reduction of sulfonylureas and insulin should be considered when prescribed in combination with miglitol.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1464489, 'Aloe Vera Leaf', 'Miglitol', 'Moderate', 'Concomitant use of oral products containing aloe vera gel may potentiate the hypoglycemic effect of insulin and other antidiabetic agents.', 'DDInter', 1767317476, 'Until more data are available, caution is advised when aloe vera is administered orally to patients receiving insulin or other antidiabetic agents due to the potential for increased risk of hypoglycemia. Patients should be monitored for changes in diabetic medication requirements.', 'Synergism', 'Until more data are available, caution is advised when aloe vera is administered orally to patients receiving insulin or other antidiabetic agents due to the potential for increased risk of hypoglycemia.', NULL, NULL, 'Moderate', 'pharmacodynamic');
