INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1464495, 'Activated charcoal', 'Miglitol', 'Moderate', 'INTERVAL: The effect of alpha-glucosidase inhibitors may be reduced when taken concomitantly with intestinal adsorbents (e.g., charcoal) or carbohydrate-splitting digestive enzymes (e.g., amylase, pancreatin).', 'DDInter', 1767317476, 'Alpha-glucosidase inhibitors should not be administered simultaneously with intestinal adsorbents or preparations containing carbohydrate-splitting digestive enzymes. The optimal dosing interval between these agents has not been established. Postprandial blood glucose and glycosylated hemoglobin (HbA1c) should be monitored to ensure adequate therapeutic response to alpha-glucosidase inhibitor therapy.', 'Absorption', 'Alpha-glucosidase inhibitors should not be administered simultaneously with intestinal adsorbents or preparations containing carbohydrate-splitting digestive enzymes.', NULL, NULL, 'Moderate', 'pharmacodynamic');
